import Form from 'react-bootstrap/Form';
import "../style/AddContact.css";
import React, { useState } from "react";

export default function AddContact(props){

    const [user, setUser] = useState({
        id: "",
        image: "",
        name: "",
        address: "",
        email: "",
        phone: "",
      });

    function onChange(event) { setUser({[event.target.name]: event.target.value}) }

return (
    <Form>
        <Form.Row>
            <Form.Group>
            <Form.Label>Name</Form.Label>
            <Form.Control
            type="text"
            name="name"
            value={user.firstName}
            onChange={onChange}
            /></Form.Group>
        </Form.Row>
        <Form.Row>
            <Form.Group>
            <Form.Label>Address</Form.Label>
            <Form.Control
            type="text"
            name="address"
            value={user.address}
            onChange={onChange}
            /></Form.Group>
        </Form.Row>
        <Form.Row>
            <Form.Group>
            <Form.Label>Email</Form.Label>
            <Form.Control
            type="text"
            name="email"
            pattern="^[\w-\+]+(\.[\w]+)@[\w-]+(\.[\w]+)(\.[a-z]{2,})$"
            value={user.email}
            onChange={onChange}
            /></Form.Group>
        </Form.Row>
        <Form.Row>
            <Form.Group>
            <Form.Label>Phone</Form.Label>
            <Form.Control
            type="text"
            name="phone"
            pattern="[0-9]{3}[0-9]{3}[0-9]{4}"
            value={user.phone}
            onChange={onChange}
            /></Form.Group>
        </Form.Row>
    </Form>       
) }
